// =============================================================================

// Using Record<string, string> to handle the wider string type from autogenerated API
// but RiskLevel union is still available for type narrowing where needed
export const RISK_LEVEL_LABELS: Record<string, string> = {
  high: "High",
  medium: "Med",
  low: "Low",
} satisfies Record<RiskLevel, string>;
